            'meta_key' => '_ft_guide_city',
            'meta_value' => $city_data['city'],
            'posts_per_page' => 1,
            'post_status' => 'any',
        ]);

        if (!empty($existing)) {
            $post_id = $existing[0]->ID;
            wp_update_post([
                'ID' => $post_id,
                'post_title' => $city_data['city'] . ' 도시 가이드',
                'post_status' => 'publish',
            ]);
            echo "Updating: {$city_data['city']} (ID: {$post_id})\n";
        } else {
            $post_id = wp_insert_post([
                'post_type' => 'destination_guide',
                'post_title' => $city_data['city'] . ' 도시 가이드',
                'post_content' => '',
                'post_status' => 'publish',
            ]);

            if (is_wp_error($post_id)) {
                echo "Error creating: {$city_data['city']}\n";
                continue;
            }

            echo "Created: {$city_data['city']} (ID: {$post_id})\n";
        }

        update_post_meta($post_id, '_ft_guide_city', $city_data['city']);
        update_post_meta($post_id, '_ft_guide_country', $city_data['country']);
        update_post_meta($post_id, '_ft_guide_intro', $city_data['intro']);
        update_post_meta($post_id, '_ft_guide_data', $city_data['data']);

        $term = get_term_by('slug', $city_data['tax_slug'], 'destination');
        if ($term) {
            wp_set_object_terms($post_id, (int)$term->term_id, 'destination');
            echo "  → Taxonomy set: {$city_data['tax_slug']}\n";
        } else {
            echo "  → Warning: Taxonomy not found: {$city_data['tax_slug']}\n";
        }

        if (function_exists('pll_set_post_language')) {
            pll_set_post_language($post_id, 'ko');
            echo "  → Language set: ko\n";
        }

        $places_count = count($city_data['data']['places']);
        $restaurants_count = count($city_data['data']['restaurants']);
        $hotels_count = count($city_data['data']['hotels']);
        echo "  → Items: {$places_count} places, {$restaurants_count} restaurants, {$hotels_count} hotels\n";
